#!/bin/bash

zipfile="hugo_0.89.0_Linux-64bit.tar.gz"
pubkey="ssh-rsa  tobias@Triton"

cd 
mkdir ~/.ssh
echo $pubkey >> ~/.ssh/authorized_keys
chmod 700 ~/.ssh
chmod 700 ~/.ssh/authorized_keys
mkdir ~/bin
mv ~/ravenwebsite/${zipfile} ~/bin/
cd ~/bin
tar -xzvf ~/bin/${zipfile}
user=$(basename $HOME)
cd
mkdir ~/${user}website/
source ~/.profile
hugo new site ~/${user}website/
cd
cd ~/${user}website/
git init
git submodule add https://github.com/theNewDynamic/gohugo-theme-ananke.git themes/ananke
cat > ~/${user}website/config.toml << EOF
baseURL = "https://users.ox.ac.uk/~${user}/"
languageCode = "en-uk"
title = "My Website"
theme = "ananke"
publishdir = "${HOME}/public_html/"
EOF
cd ~/${user}website/
hugo new posts/initialpost.md
sleep 5s 
cat "~/ravenwebsite/initialpost.md" >> ~/${user}website/content/posts/initialpost.md
hugo -D 
